import React from 'react';
import "./Task.css";

const Task = ({task, handleTaskClick}) => {
   
   return (
       <div className="task-container" style={task.completed ? {borderleft: '6px solid #eee'} : {} }>
            {task.title} {task.id}
       </div>
   )
   
    // return  <div className="task-container">{task.title}</div>;
};
 
export default Task;
